sys_script_include.script:
class NeedItUtils
{
	constructor() {}

	//Checks if date is in the past
	static isDatePast(strDate)
	{
		let rightNow = new GlideDateTime(),
			testDate = new GlideDateTime(strDate);

		return testDate.before(rightNow);
	}

	//checks if the date is today
	static isDateToday(strDate)
	{
		let today = new GlideDateTime(),
			testDate = new GlideDateTime(strDate);
		return today.getLocalDate() === testDate.getLocalDate();
	}
}
